import React from "react";
import useInicializacao from "./useInicializacao";
import useCodigo from "./useCodigo";
import SecaoSelector from "./SecaoSelector";
import AbaSelector from "./AbaSelector";
import CamposExpansor from "./CamposExpansor";
import CodigoViewer from "./CodigoViewer";

function Config({ jsonData }) {
  const {
    secaoSelecionada,
    abaSelecionada,
    mudarSecao,
    mudarAba,
    abas,
    abaAtual,
  } = useInicializacao(jsonData);

  const {
    metodoSelecionado,
    tipoCodigoSelecionado,
    tiposCodigo,
    copiarCodigo,
    baixarCodigo,
    copiado,
    setMetodoSelecionado,
    setTipoCodigoSelecionado,
  } = useCodigo(abaAtual);

  return (
    <div className="min-h-screen p-4 bg-gray-50">
      <SecaoSelector
        secoes={Object.keys(jsonData || {})}
        secaoSelecionada={secaoSelecionada}
        mudarSecao={mudarSecao}
      />

      {secaoSelecionada && abaSelecionada && (
        <>
          <AbaSelector
            abas={abas}
            abaSelecionada={abaSelecionada}
            mudarAba={mudarAba}
          />
          <CamposExpansor campos={abaAtual.campos} />
          <CodigoViewer
            metodoSelecionado={metodoSelecionado}
            setMetodoSelecionado={setMetodoSelecionado}
            tipoCodigoSelecionado={tipoCodigoSelecionado}
            setTipoCodigoSelecionado={setTipoCodigoSelecionado}
            tiposCodigo={tiposCodigo}
            copiarCodigo={copiarCodigo}
            baixarCodigo={baixarCodigo}
          />
        </>
      )}

      {copiado && (
        <div className="fixed bottom-4 left-1/2 transform -translate-x-1/2 bg-green-500 text-white py-2 px-4 rounded-lg shadow-lg">
          CÃ³digo copiado com sucesso!
        </div>
      )}
    </div>
  );
}

export default Config;
