import "./App.css";
import Esquerda from "./componentes/esquerda/esquerda";
import Topo from "./componentes/topo/topo";
import Meio from "./componentes/meio/meio";
import React, { useState, useEffect } from "react";
import Config from "./componentes/meio/janela/config/Config";
import Sistema from "./componentes/esquerda/sistema/Sistema";

function App() {
  const [jsonData, setJsonData] = useState(null);
  const [arquivoSelecionado, setArquivoSelecionado] = useState(null);

  useEffect(() => {
    if (arquivoSelecionado) {
      fetch(`/data/${arquivoSelecionado}.json`)
        .then((res) => res.json())
        .then((data) => setJsonData(data))
        .catch((err) => console.error("Erro ao carregar JSON:", err));
    }
  }, [arquivoSelecionado]);
  return (
    <div className="pagina">
      <Esquerda />
      <div className="area-direita">
        <Topo></Topo>
        <Meio>
          <div className="flex">
            <Sistema aoClicar={(nome) => setArquivoSelecionado(nome)} />
            <div className="flex-1">
              {jsonData ? (
                <Config jsonData={jsonData} />
              ) : (
                <p className="text-center text-gray-500 mt-8">
                  Selecione um item para visualizar os dados.
                </p>
              )}
            </div>
          </div>
        </Meio>
      </div>
    </div>
  );
}

export default App;
