import { useState, useEffect } from "react";

export default function useInicializacao(jsonData) {
  const [secaoSelecionada, setSecaoSelecionada] = useState(null);
  const [abaSelecionada, setAbaSelecionada] = useState(null);

  useEffect(() => {
    if (jsonData) {
      const primeiraSecao = Object.keys(jsonData)[0];
      const primeiraAba = Object.keys(jsonData[primeiraSecao].abas)[0];
      setSecaoSelecionada(primeiraSecao);
      setAbaSelecionada(primeiraAba);
    }
  }, [jsonData]);

  const mudarSecao = (secao) => {
    setSecaoSelecionada(secao);
    setAbaSelecionada(Object.keys(jsonData[secao].abas)[0]);
  };

  const mudarAba = (aba) => setAbaSelecionada(aba);

  const abas = jsonData?.[secaoSelecionada]?.abas || {};
  const abaAtual = abas?.[abaSelecionada] || null;

  return {
    secaoSelecionada,
    abaSelecionada,
    mudarSecao,
    mudarAba,
    abas,
    abaAtual,
  };
}
