// conteudosPorSecao.js (Vite)
const arquivos = import.meta.glob("./*.json", { eager: true });

export const conteudosPorSecao = Object.fromEntries(
  Object.entries(arquivos).map(([path, conteudo]) => {
    const nome = path.split("/").pop().replace(".json", "");
    return [nome, conteudo];
  })
);
