import React, { useState } from "react";
import { conteudosPorSecao } from "./conteudosPorSecao";

function Config() {
  const secoes = Object.keys(conteudosPorSecao);
  const [secaoSelecionada, setSecaoSelecionada] = useState(null); // Inicialmente sem seção selecionada
  const [abaSelecionada, setAbaSelecionada] = useState(null); // Inicialmente sem aba selecionada
  const [metodoSelecionado, setMetodoSelecionado] = useState("GET");
  const [tipoCodigoSelecionado, setTipoCodigoSelecionado] = useState("curl");
  const [copiado, setCopiado] = useState(false); // Estado para o pop-up de confirmação

  const mudarSecao = (secao) => {
    setSecaoSelecionada(secao);
    setAbaSelecionada(Object.keys(conteudosPorSecao[secao].abas)[0]); // Seleciona a primeira aba por padrão
  };

  const mudarAba = (aba) => {
    setAbaSelecionada(aba);
  };

  const mudarMetodo = (evento) => {
    setMetodoSelecionado(evento.target.value);
  };

  const mudarTipoCodigo = (evento) => {
    setTipoCodigoSelecionado(evento.target.value);
  };

  const { abas } = conteudosPorSecao[secaoSelecionada] || {}; // Verifica se a seção foi selecionada
  const abaAtual = abas ? abas[abaSelecionada] : null;

  // Obtém o conteúdo do método HTTP selecionado
  const metodoAtual = abaAtual
    ? abaAtual.metodos.find((metodo) => metodo.metodo === metodoSelecionado)
    : null;

  // Verifica se os tipos_codigo existem para o método selecionado
  const tiposCodigo = metodoAtual ? metodoAtual.tipos_codigo : null;

  // Função para copiar o código para a área de transferência
  const copiarCodigo = (codigo) => {
    navigator.clipboard
      .writeText(codigo)
      .then(() => {
        setCopiado(true); // Exibe o pop-up de confirmação
        setTimeout(() => setCopiado(false), 3000); // O pop-up desaparece após 3 segundos
      })
      .catch((err) => {
        console.error("Erro ao copiar o código: ", err);
      });
  };

  // Função para baixar o código como um arquivo
  const baixarCodigo = (codigo, tipo) => {
    const blob = new Blob([codigo], { type: "text/plain" });
    const link = document.createElement("a");
    const extensao =
      tipo === "curl"
        ? ".sh"
        : tipo === "php"
        ? ".php"
        : tipo === "node"
        ? ".js"
        : ".txt";
    link.href = URL.createObjectURL(blob);
    link.download = `codigo${extensao}`;
    link.click();
  };

  return (
    <div className="min-h-screen p-4 bg-gray-50">
      {/* Seções como botões */}
      <div className="flex space-x-4 mb-6">
        {secoes.map((secao) => (
          <button
            key={secao}
            onClick={() => mudarSecao(secao)}
            className={`px-4 py-2 rounded-full ${
              secaoSelecionada === secao
                ? "bg-blue-600 text-white"
                : "bg-gray-200"
            }`}
          >
            {secao}
          </button>
        ))}
      </div>

      {/* Conteúdo da seção */}
      {secaoSelecionada && (
        <div>
          {/* Abas */}
          <div className="flex space-x-4 border-b-2 mb-6">
            {Object.keys(abas).map((aba) => (
              <button
                key={aba}
                onClick={() => mudarAba(aba)}
                className={`pb-2 ${
                  abaSelecionada === aba
                    ? "border-b-4 border-blue-600 text-blue-600"
                    : "text-gray-600"
                }`}
              >
                {aba}
              </button>
            ))}
          </div>

          {/* Conteúdo da aba */}
          <div className="space-y-4">
            <h2 className="text-xl font-bold mb-4">Campos:</h2>

            {/* Expansores para campos */}
            <div className="space-y-2">
              {abaAtual.campos.map((campo, index) => (
                <details key={index} className="bg-white p-4 rounded shadow">
                  <summary className="cursor-pointer font-medium">
                    {campo.nome}
                  </summary>
                  <p className="mt-2 text-gray-600">{campo.descricao}</p>
                </details>
              ))}
            </div>

            {/* Seletor de métodos HTTP */}
            <h2 className="text-xl font-bold mt-8 mb-4">Métodos:</h2>
            <select
              value={metodoSelecionado}
              onChange={mudarMetodo}
              className="p-2 border rounded-md"
            >
              <option value="GET">GET</option>
              <option value="POST">POST</option>
              <option value="PUT">PUT</option>
              <option value="DELETE">DELETE</option>
            </select>

            {/* Seletor de Tipo de Código */}
            <h2 className="text-xl font-bold mt-8 mb-4">
              Selecione o Tipo de Código:
            </h2>
            <select
              value={tipoCodigoSelecionado}
              onChange={mudarTipoCodigo}
              className="p-2 border rounded-md"
            >
              {tiposCodigo &&
                Object.keys(tiposCodigo).map((tipo) => (
                  <option key={tipo} value={tipo}>
                    {tipo.toUpperCase()}
                  </option>
                ))}
            </select>

            {/* Exibindo o tipo de código após o método */}
            {tiposCodigo && (
              <section className="mt-6 p-4 bg-white rounded-md shadow-md">
                <h3 className="text-xl font-semibold">
                  Código ({tipoCodigoSelecionado.toUpperCase()}):
                </h3>
                <div className="flex items-center justify-between">
                  <pre className="bg-gray-100 p-4 rounded-md w-full">
                    <code>{tiposCodigo[tipoCodigoSelecionado]}</code>
                  </pre>
                  <div className="flex space-x-2">
                    <button
                      onClick={() =>
                        copiarCodigo(tiposCodigo[tipoCodigoSelecionado])
                      }
                      className="ml-4 px-4 py-2 bg-blue-600 text-white rounded-md"
                    >
                      Copiar
                    </button>
                    <button
                      onClick={() =>
                        baixarCodigo(
                          tiposCodigo[tipoCodigoSelecionado],
                          tipoCodigoSelecionado
                        )
                      }
                      className="ml-4 px-4 py-2 bg-green-600 text-white rounded-md"
                    >
                      Baixar
                    </button>
                  </div>
                </div>
              </section>
            )}
          </div>
        </div>
      )}

      {/* Pop-up de confirmação quando o código é copiado */}
      {copiado && (
        <div className="fixed bottom-4 left-1/2 transform -translate-x-1/2 bg-green-500 text-white py-2 px-4 rounded-lg shadow-lg">
          Código copiado com sucesso!
        </div>
      )}
    </div>
  );
}

export default Config;
