import React, { useState } from "react";
import "../topo/topo.css";

function Opcao({ titulo, opcoes }) {
  const [aberto, setAberto] = useState(false);
  const [selecionado, setSelecionado] = useState(null); // Estado para a opção selecionada

  const toggleOpcao = () => {
    setAberto(!aberto); // Alterna entre mostrar/ocultar o conteúdo
  };

  const selecionarOpcao = (opcao) => {
    setSelecionado(opcao); // Marca a opção como selecionada
    setAberto(false); // Fecha o menu ao selecionar uma opção
  };

  return (
    <div className="opcao">
      <button onClick={toggleOpcao} className="titulo-opcao">
        {titulo}
      </button>

      <div className={`conteudo-opcao ${aberto ? "aberto" : ""}`}>
        {opcoes.map((opcao, index) => (
          <button
            key={index}
            onClick={() => selecionarOpcao(opcao)}
            className={`opcao ${selecionado === opcao ? "selecionada" : ""}`}
          >
            {opcao}
          </button>
        ))}
      </div>
    </div>
  );
}

export default Opcao;
