import { useState } from "react";

export default function useCodigo(abaAtual) {
  const [metodoSelecionado, setMetodoSelecionado] = useState("GET");
  const [tipoCodigoSelecionado, setTipoCodigoSelecionado] = useState("curl");
  const [copiado, setCopiado] = useState(false);

  const metodoAtual = abaAtual?.metodos.find(
    (m) => m.metodo === metodoSelecionado
  );
  const tiposCodigo = metodoAtual?.tipos_codigo || null;

  const copiarCodigo = (codigo) => {
    navigator.clipboard.writeText(codigo).then(() => {
      setCopiado(true);
      setTimeout(() => setCopiado(false), 3000);
    });
  };

  const baixarCodigo = (codigo, tipo) => {
    const blob = new Blob([codigo], { type: "text/plain" });
    const link = document.createElement("a");
    const ext =
      tipo === "curl"
        ? ".sh"
        : tipo === "php"
        ? ".php"
        : tipo === "node"
        ? ".js"
        : ".txt";
    link.href = URL.createObjectURL(blob);
    link.download = `codigo${ext}`;
    link.click();
  };

  return {
    metodoSelecionado,
    setMetodoSelecionado,
    tipoCodigoSelecionado,
    setTipoCodigoSelecionado,
    tiposCodigo,
    copiarCodigo,
    baixarCodigo,
    copiado,
  };
}
