import React, { useState } from "react";
import "../aba/aba.css";
import dadosAbas from "../dados/dadosAbas";

function AbasDinamicas() {
  const [abas, setAbas] = useState([]);
  const [abaAtiva, setAbaAtiva] = useState("");

  const carregarAbas = (tipo) => {
    const novasAbas = dadosAbas[tipo] || [];
    setAbas(novasAbas);
    setAbaAtiva(novasAbas[0]?.id || "");
  };

  return (
    <div className="container-abas">
      {/* Botões principais */}
      <div className="botoes-principais">
        <button onClick={() => carregarAbas("cliente")}>Cliente</button>
        <button onClick={() => carregarAbas("fornecedor")}>Fornecedor</button>
        <button onClick={() => carregarAbas("produto")}>Produto</button>
      </div>

      {/* Área de abas */}
      {abas.length > 0 && (
        <>
          {/* Mostrar abas se tiver mais de uma */}
          {abas.length > 1 && (
            <div className="abas">
              {abas.map((aba) => (
                <button
                  key={aba.id}
                  className={abaAtiva === aba.id ? "aba ativa" : "aba"}
                  onClick={() => setAbaAtiva(aba.id)}
                >
                  {aba.titulo}
                </button>
              ))}
            </div>
          )}

          {/* Conteúdo da aba ativa */}
          <div className="conteudo">
            {abas.map((aba) =>
              abaAtiva === aba.id ? (
                <div key={aba.id}>
                  {/* Conteúdo puro aqui */}
                  <p>{aba.conteudo}</p>
                </div>
              ) : null
            )}
          </div>
        </>
      )}

      {/* Mensagem inicial */}
      {abas.length === 0 && (
        <p className="mensagem-vazia">
          Clique em um botão para carregar os dados.
        </p>
      )}
    </div>
  );
}

export default AbasDinamicas;
