const http = require('http');
const data = JSON.stringify({id: '123', nome: 'Cliente'});
const options = {
  hostname: 'api.exemplo.com',
  port: 443,
  path: '/cliente',
  method: 'POST',
  headers: {
    'Content-Type': 'application/json',
    'Content-Length': data.length
  }
};
const req = http.request(options, (res) => { res.on('data', (data) => { console.log(data.toString()); }); });
req.write(data);
req.end();

